import React from 'react';

const TaskPage = () => {
  return (
    <div className="container-fluid" style={{ backgroundColor: '#A1D0D0', fontFamily: 'Lexend Exa, sans-serif'}}>
      <div className="row">
        <div className="col-md-6">
          <h1>Task Better</h1>
          <form>
            <div className="mb-3">
              <label htmlFor="title" className="form-label">Title</label>
              <input type="text" className="form-control" id="title"/>
            </div>
            <div className="mb-3">
              <label htmlFor="description" className="form-label">Description</label>
              <textarea className="form-control" id="description"></textarea>
            </div>
            <button type="submit" className="btn btn-primary">Submit</button>
          </form>
        </div>

        <div className='col-md-6'>
          {/* This part can be made dynamic by mapping over an array of tasks */}
          {/* Each task would be a component */}
          {/* For simplicity, I'm hardcoding the tasks here */}

          {/* Task Component Example */}
          {/* You should create a separate Task component file for this and import it here */}

          {/* Start of Task Component */}

          {['Create Form', 'Create Tasks'].map((task, index) => (
            <>
              {index === 1 && 
                <>
                  {task}
                  This is a description of the task
                </>
              }

              {index !== 1 && 
                task
              }

              {/* Dropdown arrow icon - you can replace this with an actual icon component or element */}
              {' \u25BC'}

            </>
           ))}

           {/* End of Task Component */}

        </div>

      </div>
    </div>
  );
};

export default TaskPage;
